import CartItemContext from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/store/cart-context.js";
import { useReducer } from "react";

const defaultCartState = {
    items: [],
    totalAmount: 0
};

const cartItemReducer = (state, action) => {
    if (action.type === "ADD") {
        const newTotalAmount = state.totalAmount + action.item.price * action.item.amount;
        const currentCartItemIndex = state.items.findIndex(item => item.id === action.item.id);

        const currentCartItem = state.items[currentCartItemIndex]

        let updatedItems;

        if (currentCartItem){
            const newI = {
                ...currentCartItem,
                amount: currentCartItem.amount + action.item.amount,
            };
            updatedItems = [...state.items];
