import classes from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/Meals/MealItem/MealItemForm.Module.css"
import Input from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/UI/Input.js";
import {useRef, useState} from "react"

const MealItemForm = props => {
    const [amountIsValid, setAmountIsValid ] = useState(true);
    const amountInputRef = useRef()

    const submitHandler = (event) => {

        event.preventDefault();

        const enteredAmount = amountInputRef.current.value;
        const enteredAmountNumber  = +enteredAmount

        if (enteredAmount.trim().length === 0 || enteredAmountNumber < 1 || enteredAmountNumber > 5){
            setAmountIsValid(false);
            return;
        }


    }
    return <form className="form" onSubmit = {submitHandler}>
        <Input 
        ref = {amountInputRef}
        label = "Amount" 
        input={{
            id: "amount",
            type: "number",
            min: "1",
            max: "5",
            step: "1",
            defaultValue: "1"
        }} />
        <button> + Add</button>
        {!amountIsValid && <p>Please enter a valid amount (1 - 5)</p>}
    </form>
}

export default MealItemForm;
