import { useContext, useState } from "react";
import IconCart from "../Cart/IconCart";
import CartContext from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/store/cart-context.js"
import classes from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/Layout/Layout.css";

const CartHeaderButton = props => {
    const cartContextVar = useContext(CartContext);
    const numOrderedItems = cartContextVar.items.reduce((curOrderNumber, item) => {
        return curOrderNumber + item.amount;
    }, 0);

    return (
    <button className="button" onClick = {props.onClick}>
        <span className="icon">
            <IconCart/>
        </span>
        //Allows us to view the total number of ordered items from the home page

        <span>Your Cart</span>
        <span className="badge"> {numOrderedItems}</span>
    </button>
    )
};

export default CartHeaderButton;
