import { Fragment, useState } from "react";
import Header from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/Layout/Header.js";
import Meals from "./components/Meals/Meals";
import Cart from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/Cart/Cart.js";
import CartProvider from "./store/cart-provider";

function App() {

    const [cartIsShown, setcartIsShown] = useState(false);

    const showCartHandler = () => {
      setcartIsShown(true)
    }

    const hideCartHandler = () => {
      setcartIsShown(false)
    }

  return (
    <CartProvider>
      {cartIsShown && <Cart onClose= {hideCartHandler} />}
      <Header onShowCart = {showCartHandler}/>
      <main>
        <Meals/>
      </main>
    </CartProvider>
  );
}

export default App;
