import Modal  from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/UI/Modal.js";
import classes from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/Cart/Cart.Module.css"
import { useContext } from "react";
import CartContext from "../../store/cart-context";
import CartItem from "./CartItem";


const Cart = (props) => {
    const cartCtx = useContext(CartContext);

    const totalAmount = cartCtx.totalAmount ? `$${cartCtx.totalAmount.toFixed(2)}` : '$0.00';

    const hasItems = cartCtx.items.length > 0;

    const cartItemAddHandler = item => {};

    const cartItemRemoveHandler = item => {};

    const cartItems = <ul className="cart-items">{
        cartCtx.items.map(item => (
        <CartItem key={item.id} name = {item.name} amount = {item.amount} price = {item.price} onRemove = {cartItemRemoveHandler} />))}
        </ul>

        return <Modal onClose= {props.onClose}>
            {cartItems}
        <div className="total">
            <span>Total amount</span>
            <span>{totalAmount}</span>
        </div>
        <div className="actions">
            <button className="button--alt" onClick= {props.onClose} >Close</button>
            {hasItems && <button className="button">Order</button>}
        </div>
        </Modal>

}

export default Cart;
