import { useContext, useState } from "react";
import IconCart from "../Cart/IconCart";
import CartContext from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/store/cart-context.js"
import classes from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/Layout/Layout.cs";

const CartHeaderButton = props => {
    const cartCtx = useContext(CartContext);
    const numberOfCartItems = cartCtx.items.reduce((curNumber, item) => {
        return curNumber + item.amount;
    }, 0);

    return (
    <button className="button" onClick = {props.onClick}>
        <span className="icon">
            <IconCart/>
        </span>

        <span>Your Cart</span>
        <span className="badge"> {numberOfCartItems}</span>
    </button>
    )
};

export default CartHeaderButton;
