import Modal  from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/UI/Modal.js";
import classes from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/Cart/Cart.css"
import { useContext } from "react";
import CartContext from "../../store/cart-context";
import CartItem from "./CartItem";


const Cart = (props) => {
    const cartContextVar = useContext(CartContext);
    const hasItems = cartContextVar.items.length > 0;
    const totalCartAmount = `$${cartContextVar.totalAmount.toFixed(2)}`;


    const cartItemAddHandler = (item) => {
        cartContextVar.addItem({...item, amount: 1});
    };

    const cartItemRemoveHandler = (id) => {
        cartContextVar.removeItem(id);
    };

    const cartItems = <ul className="cart-items">{
        cartContextVar.items.map((item) => (
        <CartItem 
        key={item.id} 
        name = {item.name} 
        amount = {item.amount} 
        price = {item.price} 
        onRemove = {cartItemRemoveHandler.bind(null, item.id)} 
        onAdd = {cartItemAddHandler.bind(null, item)} 
        />
        ))}
        </ul>

        return <Modal onClose= {props.onClose}>
            {cartItems}
        <div className="total">
            <span>Total amount</span>
            <span>{totalCartAmount}</span>
        </div>
        <div className="actions">
            <button className="button--alt" onClick= {props.onClose} >Close</button>
            {hasItems && <button className="button">Order</button>}
        </div>
        </Modal>

}

export default Cart;
