import classes from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/Meals/Meals.css"
import Input from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/UI/Input.js";
import {useRef, useState} from "react"

const MealItemFormObject = props => {
    const [amountIsValid, setAmountIsValid ] = useState(true);
    const checkAmountValidity = useRef()

    const submitHandler = (event) => {

        event.preventDefault();

        const enteredAmount = checkAmountValidity.current.value;
        const enteredAmountNumber  = +enteredAmount

        if (enteredAmount.trim().length === 0 || enteredAmountNumber < 1 || enteredAmountNumber > 5){
            setAmountIsValid(false);
            return;
        }
        props.onAddToCart(enteredAmountNumber);
    }
    return (<form className="form" onSubmit = {submitHandler}>
        <Input 
        ref = {checkAmountValidity}
        label = "Amount" 
        input={{
            id: "amount_" + props.id,
            type: "number",
            min: "1",
            max: "5",
            step: "1",
            defaultValue: "1"
        }} />
        <button> + Add</button>
        {!amountIsValid && <p>Please enter a valid amount (1 - 5).</p>}
    </form>
    );
}

export default MealItemFormObject;
