import classes from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/Meals/Meals.css"
import MealItemForm from "/Users/chiamakabrowneyes/Downloads/01-starting-project/src/components/Meals/MealItemForm.js"
import {useContext} from "react";
import CartContext from "../../store/cart-context";

const MealItem = props => {
    const cartCtx = useContext(CartContext);

    const price =   `$${props.price.toFixed(2)}`

    const addToCartHandler = amount => {
        cartCtx.addItem({
            id: props.id,
            name: props.name,
            amount: amount,
            price: props.price,
        })
    };

    return <li className= "meals">
        <div>
            <h3 >{props.name}</h3>
            <div className= "description">{props.description}</div>
            <div className= "price">{price}</div>
            </div>
        <div>
            <MealItemForm id={props.id} onAddToCart = {addToCartHandler}/>
        </div>
    </li>
}

export default MealItem;
